import React, { useEffect, useState, useRef } from "react";
import axios from "axios";
import loadingGif from "./loading.gif";
import "./style/App.scss";
import PhotoCard from "./PhotoCard";

function App() {
  const [photos, setPhotos] = useState([]);
  const [term, setTerm] = useState("");
  const [pageNumber, setPageNumber] = useState(1);
  const [termErr, setTermErr] = useState("");
  const [loading, setLoading] = useState(false);
  const pageEnd = useRef();
  //count page loading content
  let num = 1;

  useEffect(() => {
    if (loading) {
      const observer = new IntersectionObserver(
        (entries) => {
          if (entries[0].isIntersecting) {
            num++;
            loadMore();
            if (num >= 10) {
              observer.unobserve(pageEnd.current);
            }
          }
        },
        { threshold: 1 }
      );
      observer.observe(pageEnd.current);
    }
  }, [loading]);

  const fetchPhotos = async (term, pageNumber) => {
    const response = await axios.get("https://api.unsplash.com/photos/", {
      params: {
        query: term,
        page: pageNumber,
        per_page: 10,
      },
      headers: {
        Authorization: "Client-ID _baTwPmANwCJ6ziqRMoeW6gR3DAVn0-MT7eLFKjCss4",
      },
    });
    setPhotos((p) => [...p, ...response.data]);
    setLoading(true);
    // console.log(response.data);
  };

  const handleChange = (e) => {
    setTerm(e.target.value);
  };

  // Validation
  const validate = () => {
    let isValid = true;
    if (term === "") {
      setTermErr("**This field is required");
      isValid = false;
    }
    return isValid;
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const isValid = validate();

    if (isValid) {
      fetchPhotos(term, pageNumber);
      // console.log(photos);
      setTermErr("");
    }
  };

  const loadMore = () => {
    setPageNumber((prevPageNumber) => prevPageNumber + 1);
  };

  return (
    <>
      <div className="header">
        <h1 className="title">Search for images you want to browse.</h1>
        <h3>Happy Scrolling!</h3>
        <input
          type="text"
          value={term}
          onChange={handleChange}
          placeholder="Search here..."
        />
        <div className="empty-field-err">{termErr}</div>
        <button type="submit" onClick={handleSubmit}>
          Submit
        </button>
      </div>
      <div className="result-section">
        <div className="photo-container">
          {photos.map((photo, index) => {
            return (
              // <div className="photo-block" key={index}>
              //   <img
              //     ref={imageRef}
              //     src={photo.urls.small}
              //     alt="unsplash source"
              //   />
              // </div>
              <PhotoCard key={index} image={photo} />
            );
          })}
        </div>
        <button className="load-more" onClick={loadMore} ref={pageEnd}>
          Load More
        </button>
        <div className="loading">
          <img src={loadingGif} alt="loading animation" />
        </div>
      </div>
    </>
  );
}

export default App;
