import React, { useEffect, useState, useRef } from "react";
import axios from "axios";
import loading from "./loading.gif";
import style from "./style/App.scss";

function App() {
  const [photos, setPhotos] = useState([]);
  const [term, setTerm] = useState("");
  const [pageNumber, setPageNumber] = useState(1);
  const [termErr, setTermErr] = useState("");

  const fetchPhotos = async (term, pageNumber) => {
    const response = await axios.get("https://api.unsplash.com/photos/", {
      params: {
        query: term,
        page: pageNumber,
        per_page: 4,
      },
      headers: {
        Authorization: "Client-ID _baTwPmANwCJ6ziqRMoeW6gR3DAVn0-MT7eLFKjCss4",
      },
    });
    setPhotos(response.data);
    console.log(response.data);
  };

  const handleChange = (e) => {
    setTerm(e.target.value);
  };

  // Validation
  const validate = () => {
    let isValid = true;
    if (term === "") {
      setTermErr("**This field is required");
      isValid = false;
    }
    return isValid;
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const isValid = validate();

    if (isValid) {
      fetchPhotos(term, pageNumber);
      // console.log(photos);
      setTermErr("");
    }
  };

  // useEffect(() => {
  //   fetchPhotos(term);
  // }, []);

  return (
    <>
      <div className="header">
        <h1 className="title">Search for images you want to browse.</h1>
        <h3>Happy Scrolling!</h3>
        <input
          type="text"
          value={term}
          onChange={handleChange}
          placeholder="Search here..."
        />
        <div className="empty-field-err">{termErr}</div>
        <button type="submit" onClick={handleSubmit}>
          Submit
        </button>
      </div>
      <div className="result-section">
        {photos.map((photo, index) => {
          return (
            <div className="page-block" key={index}>
              <img src={photo.urls.small} alt="unsplash source" />
              <p>{photo.user.first_name + " " + photo.user.last_name}</p>
              <span>Likes on this post: {photo.user.total_likes}</span>
            </div>
          );
        })}
        <div className="loading">{loading}</div>
      </div>
    </>
  );
}

export default App;
