import React, { useEffect, useState, useRef } from "react";
import axios from "axios";
import style from "./style/App.scss";

function App() {
  const [photos, setPhotos] = useState([]);
  const [term, setTerm] = useState("");
  const [pageNumber, setPageNumber] = useState(1);
  const [termErr, setTermErr] = useState("");

  const fetchPhotos = async (term, pageNumber) => {
    const response = axios.get(`https://api.unsplash.com/photos/`, {
      params: {
        query: term,
        page: pageNumber,
        per_page: 10,
      },
      headers: {
        Authorization: "Client-ID _baTwPmANwCJ6ziqRMoeW6gR3DAVn0-MT7eLFKjCss4",
      },
    });
    const data = (await response).data;
    setPhotos(data);
  };

  const handleChange = (e) => {
    setTerm(e.target.value);
  };

  // Validation
  const validate = () => {
    let isValid = true;
    if (term === "") {
      setTermErr("Dont leave this empty!");
      isValid = false;
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (term === "") {
      alert("Search!");
    } else {
      fetchPhotos(term);
    }
    return isValid;
  };

  // useEffect(() => {
  //   fetchPhotos(term);
  // }, []);

  return (
    <>
      <div className="header">
        <h1 className="title">Search for images you want to browse.</h1>
        <h3>Happy Scrolling!</h3>
        <input
          type="text"
          value={term}
          onChange={handleChange}
          placeholder="Search here..."
        />
        <button type="submit" onClick={handleSubmit}>
          Submit
        </button>
      </div>
      <div className="result-section"></div>
    </>
  );
}

export default App;
